    findPath(grid, startX, startY, endX, endY, size) {
        var easystar = new EasyStar.js();

        easystar.setGrid(grid);

        easystar.setAcceptableTiles([0]);
        var self = this;
        easystar.findPath(startX, startY, endX, endY, function(path) {
            if (path === null) {
                alert("Path was not found");
            } else {
                self.drawPath(path, size);
                console.log(path);
            }
        });
        easystar.calculate();
    }

    drawPath(path, size) {
        var context = this.canvasObj.getContext("2d");
        for (var i = 0; i < path.length; i++) {
          context.fillStyle="blue";
          context.fillRect((path[i].x) * size, (path[i].y) * size, size, size);
        }
    }

